public class substraction {

	/**
	 * @param args
	 * 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int value1;
		int value2;

		int number1 = 132425;
		int number2 = 6780567;
		int number3 = 2134;

		value1 = number2 - number1;
		value2 = number1 - number3;

		System.out.println("The value of " + number2 + " - " + number1 + " is: " + value1);

		System.out.println("The value of " + number1 + " - " + number3 + " is: " + value2);

	}

}
